mapper.select:
SELECT EXISTS (
    SELECT 1
    FROM city_liked
    WHERE user_id = #{userId} AND city_id = #{cityId}
) AS liked

SELECT EXISTS (
    SELECT 1
    FROM board_liked
    WHERE user_id = #{userId} AND board_id = #{peedId}
) AS liked

SELECT EXISTS (
    SELECT 1
    FROM package_liked
    WHERE user_id = #{userId} AND package_id = #{packageId}
) AS liked
